import React, { useState } from "react";
import "./css/firstname.css";
import { useNavigate } from "react-router-dom";
import logo from "../../assets/logo.png";

const FirstNameForm = () => {
  const [firstName, setFirstName] = useState(""); // Store first name
  const navigate = useNavigate();

  const handleNext = async () => {
    if (!firstName.trim()) {
      alert("Please enter your first name");
      return;
    }

    try {
      const response = await fetch("http://localhost:5000/users", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ name: firstName, goal: "" }), // Sending name with empty goal for now
      });

      if (!response.ok) {
        throw new Error("Failed to save data");
      }

      const data = await response.json();
      console.log("User saved:", data);

      localStorage.setItem("firstName", firstName); // Store name in localStorage
      navigate("/goalselector"); // Navigate to next page
    } catch (error) {
      console.error("Error:", error);
      alert("Failed to save your data. Please try again.");
    }
  };

  return (
    <div className="navbar">
      <div className="logo" onClick={() => navigate("/homepage")}>
        <img src={logo || "/placeholder.svg"} alt="logo" className="logo-image" />
      </div>
      <div className="form-container">
        <div className="progress-bar">
          <div className="progress" style={{ width: "20%" }}></div>
        </div>
        <h2>Whatâ€™s your First name?</h2>
        <input
          type="text"
          placeholder="First Name"
          className="input-field"
          value={firstName}
          onChange={(e) => setFirstName(e.target.value)}
        />
        <div className="button-group">
          <button className="back-btn" onClick={() => navigate("/login")}>BACK</button>
          <button className="next-btn" onClick={handleNext}>NEXT</button>
        </div>
      </div>
    </div>
  );
};

export default FirstNameForm;
